CREATE TABLE `wild`.`pages` ( `id` INT(11) NOT NULL AUTO_INCREMENT , `group_id` VARCHAR(255) NOT NULL , `meta_title` VARCHAR(255) NULL , `meta_description` VARCHAR(255) NULL , `meta_keywords` VARCHAR(255) NULL , `is_deleted` INT NULL DEFAULT '0' , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL , `language` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
